export {};

import React, { useEffect } from 'react';
import gjsPresetWebpage from "grapesjs-preset-webpage";
import gjsBlockBasic from "grapesjs-blocks-basic";
import grapesjs, { Editor } from "grapesjs";

const GrapeJSEditor: React.FC = () => {
  useEffect(() => {
    const editor = grapesjs.init({
      container: '#gjs',
      storageManager: false, // Disable storage manager for simplicity
      plugins: ['gjs-preset-webpage'], // Use the Webpage preset
      // Configure GrapeJS options as needed
    });

    return () => {
      editor.destroy();
    };
  }, []);

  return (
    <div>
      <h2>GrapeJS Web Page Editor</h2>
      <div id="gjs" style={{ height: '400px' }} />
    </div>
  );
};

export default GrapeJSEditor;
