// src/components/NumberCounter.tsx
import React, { useEffect } from 'react';

interface NumberCounterProps {
  htmlCode: string; // Pass the HTML code from GrapesJS as a prop
}

const NumberCounter: React.FC<NumberCounterProps> = ({ htmlCode }) => {
  useEffect(() => {
    const container = document.getElementById('counter-container');
    if (container) {
      container.innerHTML = htmlCode;
      // You can add any necessary JavaScript logic here
    }
  }, [htmlCode]);

  return <div id="counter-container" />;
};

export default NumberCounter;
