// src/App.tsx
import React from 'react';
import NumberCounter from './NumberCounter';

const App: React.FC = () => {
  // Define the incrementCounter function in the global scope
  window.incrementCounter = () => {
    count++;
    const counterElement = document.getElementById('counter');
    if (counterElement) {
      counterElement.textContent = count.toString();
    }
  };

  // HTML code exported from GrapesJS
  const htmlCode = `
    <div>
      <h2>Counter:</h2>
      <div id="counter">0</div>
      <button onclick="incrementCounter()">Increment</button>
    </div>
  `;

  let count = 0;

  return (
    <div className="App">
      <h1>React Number Counter with GrapesJS</h1>
      <NumberCounter htmlCode={htmlCode} />
    </div>
  );
};

export default App;
