import React, { useEffect } from 'react';
import grapesjs from 'grapesjs';

const GrapesJSCounter: React.FC = () => {
  useEffect(() => {
    const editor = grapesjs.init({
      container: '#gjs',
      components: '<div class="counter">0</div>',
      style: '.counter { font-size: 24px; }',
    });

    // Update GrapeJS content when the count changes
    const updateCounter = (count: number) => {
      const counterComponent = editor.DomComponents.getWrapper().find('.counter');
      counterComponent.setAttributes({ 'data-count': count });
      counterComponent.set('content', count.toString());
    };

    // Subscribe to count changes
    // You can replace this with your state management system
    let count = 0;
    const interval = setInterval(() => {
      count++;
      updateCounter(count);
    }, 1000);

    return () => {
      clearInterval(interval);
    };
  }, []);

  return (
    <div>
      <h2>GrapeJS Counter</h2>
      <div id="gjs" style={{ height: '200px' }} />
    </div>
  );
};

export default GrapesJSCounter;
