import React, { useEffect, useState } from 'react';
import grapesjs from 'grapesjs';

interface GrapeJSEditorProps {
  start: number;
  end: number;
}

const GrapeJSEditor: React.FC<GrapeJSEditorProps> = ({ start, end }) => {
  const [count, setCount] = useState(start);
  const [increment, setIncrement] = useState(1);

  useEffect(() => {
    if (start < end) {
      setIncrement(1);
    } else if (start > end) {
      setIncrement(-1);
    } else {
      setIncrement(0);
    }
  }, [start, end]);

  useEffect(() => {
    if (count !== end) {
      const timer = setInterval(() => {
        setCount(count + increment);
      }, 1000);

      return () => {
        clearInterval(timer);
      };
    }
  }, [count, end, increment]);

  useEffect(() => {
    const editor = grapesjs.init({
      container: '#gjs',
      storageManager: false,
    });

    // Load the Webpage preset manually
    editor.Panels.removeButton('views', 'open-layers');
    editor.Panels.removeButton('views', 'open-blocks');
    editor.Panels.addButton('views', [
      {
        id: 'open-traits',
        className: 'fa fa-cog',
        command: 'show-traits',
        attributes: { title: 'Traits' },
      },
      {
        id: 'open-style',
        className: 'fa fa-paint-brush',
        command: 'show-styles',
        attributes: { title: 'Styles' },
      },
    ]);

    editor.addComponents('<div class="counter">0</div>', {
      layer: 0,
      at: 0,
    });

    return () => {
      editor.destroy();
    };
  }, []);

  useEffect(() => {
    const counterComponent = document.querySelector('.counter');
    if (counterComponent) {
      counterComponent.textContent = count.toString();
    }
  }, [count]);

  return (
    <div>
      <h2>GrapeJS Web Page Editor</h2>
      <div id="gjs" style={{ height: '400px' }} />
      <h2>Number Counter</h2>
      <div className="counter">{count}</div>
    </div>
  );
};

export default GrapeJSEditor;
